mapper.select:
		SELECT * FROM ( SELECT /*+INDEX_DESC(notice pk_notice)*/ 
								rownum rn, noticeNo, noticeTitle, noticeContent, noticeView, noticeDate FROM notice WHERE		

  		 rownum <= #{pageNum} * #{amount}) where rn > (#{pageNum}-1) * #{amount}

mapper.update:
UPDATE notice
SET noticeTitle = #{noticeTitle},
	noticeContent = #{noticeContent}
WHERE noticeNo = #{noticeNo}

UPDATE notice
SET noticeView = noticeView + 1
WHERE noticeNo = #{noticeNo}
